import React, { useState, useEffect, useMemo } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Header from './views/components/Header';
import CartController from './controllers/CartController';
import Footer from './views/components/Footer';
import AppRouter from './routers/AppRouter';
import ToastMessage from './views/components/ToastMessage';
import AuthController from './controllers/AuthController';
import CartService from './services/CartService';

const App = () => {
  const authController = new AuthController();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastType, setToastType] = useState('info');

  // TẠO CartService VỚI isAuthenticated & userId
  const cartService = useMemo(() => {
    return new CartService(isAuthenticated, userId);
  }, [isAuthenticated, userId]);

  // TẠO CartController DỰA TRÊN CartService
  const cartController = useMemo(() => {
    return new CartController(cartService);
  }, [cartService]);

  // ĐĂNG NHẬP / ĐĂNG XUẤT → ĐỒNG BỘ GIỎ HÀNG
  useEffect(() => {
    async function checkAuth() {
      const user = await authController.getCurrentUser();
      const authenticated = !!user;
      setIsAuthenticated(authenticated);
      setUserId(user?.id || null);
      const storedCart = cartController.getCartItems();
      if (storedCart.length > 0 && JSON.stringify(storedCart) !== JSON.stringify(cartItems)) {
        setCartItems(storedCart);
      }
    }
    checkAuth();
  }, [cartItems, authController]);

  

  const addToCart = (product) => {
    try {
      const updatedCart = cartController.addToCart(product);
      setCartItems([...updatedCart]);
      setToastMessage(`Đã thêm "${product.name}" vào giỏ hàng`);
      setToastType('success');
      setShowToast(true);
    } catch (error) {
      setToastMessage(error.message);
      setToastType('danger');
      setShowToast(true);
    }
  };

  const removeFromCart = (productId) => {
    try {
      const updatedCart = cartController.removeFromCart(productId);
      setCartItems([...updatedCart]);
    } catch (error) {
      setToastMessage(error.message);
      setToastType('danger');
      setShowToast(true);
    }
  };

  const onLogin = async (email, password) => {
    const result = await authController.login(email, password);
    if (result.success) {
      setIsAuthenticated(true);
      setToastMessage('Đăng nhập thành công');
      setToastType('success');
      setShowToast(true);
      return true;
    } else {
      setIsAuthenticated(false);
      setToastMessage(result.message);
      setToastType('danger');
      setShowToast(true);
      return false;
    }
  };

  const onRegister = async (email, password, firstName, lastName, phoneNumber, address) => {
    const result = await authController.register({ email, password, firstName, lastName, phoneNumber, address: [address] });
    if (result.success) {
      setIsAuthenticated(true);
      setToastMessage('Đăng ký thành công');
      setToastType('success');
      setShowToast(true);
      return true;
    } else {
      setIsAuthenticated(false);
      setToastMessage(result.message);
      setToastType('danger');
      setShowToast(true);
      return false;
    }
  }

  const onLogout = async () => {
    const result = await authController.logout();
    if (result.success) {
      setIsAuthenticated(false);
      setToastMessage('Đã đăng xuất');
      setToastType('success');
      setShowToast(true);
    } else {
      setToastMessage(result.message);
      setToastType('danger');
      setShowToast(true);
    }
  };

  const onCartChange = (updatedCart) => {
    setCartItems([...updatedCart]);
  };

  return (
    <>
      <Header
        cartController={cartController}
        isAuthenticated={isAuthenticated}
        onLogout={onLogout}
        cartItems={cartItems}
        onCartChange={onCartChange}
      />
      <div className="">
        <AppRouter
          isAuthenticated={isAuthenticated}
          cartItems={cartItems}
          addToCart={addToCart}
          removeFromCart={removeFromCart}
          onLogin={onLogin}
          onRegister= {onRegister}
          onCartChange={onCartChange}
          authController={authController}
        />
      </div>
      <Footer />
      <div className="toast-container position-fixed bottom-0 top-0 end-0">
        <ToastMessage
          show={showToast}
          message={toastMessage}
          type={toastType}
          onClose={() => setShowToast(false)}
        />
      </div>
    </>
  );
};

export default App;
