// src/controllers/CartController.js
import CartService from '../services/CartService.js';

class CartController {
  constructor() {
    this.service = new CartService();
  }

  addToCart(product) {
    return this.service.addToCart(product);
  }

  removeFromCart(id) {
    return this.service.removeFromCart(id);
  }

  updateQuantity(id, qty) {
    return this.service.updateQuantity(id, qty);
  }

  clearCart() {
    return this.service.clearCart();
  }

  getCartItems() {
    return this.service.getCartItems();
  }

  getTotalQuantity() {
    return this.service.getTotalQuantity();
  }

  getTotalPrice() {
    return this.service.getTotalPrice();
  }
}

export default CartController;
