// src/controllers/CartController.js
class CartController {
  constructor(cartService) {
    this.cartService = cartService;
  }
  addToCart(product) { return this.cartService.addToCart(product); }
  removeFromCart(id) { return this.cartService.removeFromCart(id); }
  updateQuantity(id, qty) { return this.cartService.updateQuantity(id, qty); }
  clearCart() { return this.cartService.clearCart(); }
  getCartItems() { return this.cartService.getCartItems(); }
  getTotalPrice() { return this.cartService.getTotalPrice(); }
}
export default CartController;
