import BannerService from '../services/BannerService.js';

class BannerController {
  constructor() {
    this.bannerService = new BannerService();
  }

  getAllBanners(type = null) {
    try {
      const banners = this.bannerService.getAllBanners(type);
      if (!banners || !Array.isArray(banners)) {
        throw new Error('Danh sách banner không hợp lệ');
      }
      return banners;
    } catch (error) {
      console.error('Lỗi khi lấy danh sách banner:', error);
      return [];
    }
  }

  async getActiveBanners(type = null) {
  return await this.bannerService.getActiveBanners(type);
}

}

export default BannerController;
